mapper.update:
     UPDATE  BOARD
	     SET    STEP = STEP + 1
	     WHERE  MENU_IDX = #{ menu_idx } 
	     AND    NREF     = #{ nref }
	     AND    STEP     > #{ step }

mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER( ORDER BY NREF DESC, STEP ASC) RNUM, BOARD_IDX, MENU_IDX, BOARD_TITLE, USER_ID, READCOUNT, ( SELECT COUNT(FILE_NUM) FROM FILES F WHERE B.BOARD_IDX = F.BOARD_IDX ) FILESCOUNT, TO_CHAR(BOARD_REGDATE, 'YYYY-MM-DD HH24:MI:SS' ) BOARD_REGDATE, BNUM, LVL, STEP, NREF FROM BOARD B WHERE 1=1
AND MENU_IDX = #{menu_idx}
) T WHERE RNUM BETWEEN #{startnum} AND #{endnum}

mapper.update:
UPDATE   BOARD
 SET     board_title  = #{ board_title },
         BOARD_CONT   = #{ board_cont  }
 WHERE   BOARD_IDX    = ${ board_idx   }
